I'll create an `OTPInput` component in `src/components/OTPInput.tsx` for your React frontend project. This component will allow users to enter a 6-digit OTP, style it with Tailwind CSS, and handle input and submission.

```json
{
  "operation": "create",
  "path": "src/components/OTPInput.tsx",
  "content": "import React, { useState, useRef, ChangeEvent, KeyboardEvent, useEffect } from 'react';

interface OTPInputProps {
  length?: number;
  onComplete: (otp: string) => void;
  // You can add more props like `isLoading`, `error`, etc.
}

const OTPInput: React.FC<OTPInputProps> = ({
  length = 6,
  onComplete,
}) => {
  const [otp, setOtp] = useState<string[]>(new Array(length).fill(''));
  const [error, setError] = useState<string | null>(null);
  const inputRefs = useRef<Array<HTMLInputElement | null>>([]);

  useEffect(() => {
    if (inputRefs.current[0]) {
      inputRefs.current[0].focus();
    }
  }, []);

  const handleChange = (e: ChangeEvent<HTMLInputElement>, index: number) => {
    const value = e.target.value;
    // Allow only single digit numbers
    if (!/^[0-9]$/.test(value) && value !== '') {
      return; // Do not update if not a digit or empty
    }

    const newOtp = [...otp];
    newOtp[index] = value;
    setOtp(newOtp);
    setError(null); // Clear error on new input

    // Move focus to the next input if a digit was entered and it's not the last input
    if (value !== '' && index < length - 1) {
      inputRefs.current[index + 1]?.focus();
    } else if (value === '' && index > 0) {
      // Optionally move focus back if digit is cleared
      // inputRefs.current[index - 1]?.focus();
    }
  };

  const handleKeyDown = (e: KeyboardEvent<HTMLInputElement>, index: number) => {
    if (e.key === 'Backspace' && otp[index] === '' && index > 0) {
      e.preventDefault(); // Prevent default backspace behavior (e.g., navigating back in browser)
      const newOtp = [...otp];
      newOtp[index - 1] = ''; // Clear the previous input
      setOtp(newOtp);
      inputRefs.current[index - 1]?.focus(); // Move focus to the previous input
    } else if (e.key === 'ArrowLeft' && index > 0) {
      inputRefs.current[index - 1]?.focus();
    } else if (e.key === 'ArrowRight' && index < length - 1) {
      inputRefs.current[index + 1]?.focus();
    }
  };

  const handlePaste = (e: React.ClipboardEvent<HTMLInputElement>) => {
    e.preventDefault();
    const pasteData = e.clipboardData.getData('text').trim();

    // Check if the pasted data is a valid OTP (e.g., all digits and correct length)
    if (/^\d+$/.test(pasteData) && pasteData.length === length) {
      const newOtp = pasteData.split('');
      setOtp(newOtp);
      setError(null);
      // Optionally, automatically submit or focus elsewhere after paste
      inputRefs.current[length - 1]?.focus();
    } else {
      setError(`Please paste a ${length}-digit numeric code.`);
    }
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    const fullOtp = otp.join('');

    if (fullOtp.length === length && /^\d+$/.test(fullOtp)) {
      setError(null);
      onComplete(fullOtp);
    } else {
      setError(`Please enter a complete ${length}-digit OTP.`);
    }
  };

  return (
    <div className="flex flex-col items-center justify-center min-h-screen bg-gray-50 dark:bg-gray-900 p-4">
      <div className="bg-white dark:bg-gray-800 p-8 rounded-lg shadow-md max-w-sm w-full">
        <h2 className="text-2xl font-bold text-gray-800 dark:text-white mb-6 text-center">Enter OTP</h2>
        <form onSubmit={handleSubmit}>
          <div className="flex justify-center space-x-2 mb-6">
            {otp.map((digit, index) => (
              <input
                key={index}
                type="text"
                inputMode="numeric"
                pattern="[0-9]"
                maxLength={1}
                value={digit}
                onChange={(e) => handleChange(e, index)}
                onKeyDown={(e) => handleKeyDown(e, index)}
                onPaste={index === 0 ? handlePaste : undefined}
                ref={(el) => (inputRefs.current[index] = el)}
                className={`w-12 h-12 text-center text-2xl font-bold border rounded-md
                           focus:outline-none focus:ring-2 focus:ring-blue-500 dark:focus:ring-blue-400
                           ${error ? 'border-red-500' : 'border-gray-300 dark:border-gray-600'}
                           bg-white dark:bg-gray-700 text-gray-900 dark:text-white
                           transition duration-150 ease-in-out
                          `}
              />
            ))}
          </div>

          {error && (
            <p className="text-red-500 text-sm mb-4 text-center dark:text-red-400">
              {error}
            </p>
          )}

          <button
            type="submit"
            className="w-full px-4 py-2 bg-blue-600 text-white font-semibold rounded-md
                       hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-offset-2
                       dark:bg-blue-500 dark:hover:bg-blue-600 dark:focus:ring-blue-400 dark:focus:ring-offset-gray-900
                       transition duration-150 ease-in-out
                       disabled:opacity-50 disabled:cursor-not-allowed"
            disabled={otp.some(d => d === '')}
          >
            Verify OTP
          </button>
        </form>
      </div>
    </div>
  );
};

export default OTPInput;
"
}
