I'll create an `OtpInput` React component. Assuming a standard React project structure for a frontend React project, I'll place it in `src/components/OtpInput.tsx`. This component will allow users to input a 6-digit OTP, include basic validation, handle paste functionality, and be styled with Tailwind CSS, along with TypeScript types.

```json
{
  "operation": "create",
  "path": "src/components/OtpInput.tsx",
  "content": "import React, { useState, useRef, ChangeEvent, KeyboardEvent } from 'react';

interface OtpInputProps {
  length?: number;
  onSubmit: (otp: string) => void;
  isLoading?: boolean;
  error?: string;
}

const OtpInput: React.FC<OtpInputProps> = ({
  length = 6,
  onSubmit,
  isLoading = false,
  error,
}) => {
  const [otp, setOtp] = useState<string[]>(new Array(length).fill(''));
  const inputRefs = useRef<Array<HTMLInputElement | null>>(
    new Array(length).fill(null)
  );

  const handleChange = (
    e: ChangeEvent<HTMLInputElement>,
    index: number
  ): void => {
    const value = e.target.value;
    if (!/^\d*$/.test(value)) return; // Allow only digits

    const newOtp = [...otp];
    newOtp[index] = value.slice(-1); // Take only the last character if multiple are typed

    setOtp(newOtp);

    // Focus next input if a digit was entered and it's not the last input
    if (value && index < length - 1) {
      inputRefs.current[index + 1]?.focus();
    }
  };

  const handleKeyDown = (
    e: KeyboardEvent<HTMLInputElement>,
    index: number
  ): void => {
    // Move to previous input on backspace if current is empty or if at the start
    if (e.key === 'Backspace' && (!otp[index] || e.currentTarget.selectionStart === 0) && index > 0) {
      e.preventDefault(); // Prevent default backspace behavior if we are handling movement
      inputRefs.current[index - 1]?.focus();
      const newOtp = [...otp];
      newOtp[index - 1] = ''; // Clear the previous input when moving back
      setOtp(newOtp);
    } else if (e.key === 'Backspace' && otp[index] && e.currentTarget.selectionStart === 1) {
        // If there's a digit and cursor is at end, backspace clears it.
        // If cursor is at start and there's a digit, backspace clears current
        const newOtp = [...otp];
        newOtp[index] = '';
        setOtp(newOtp);
    }

    // Handle arrow keys for navigation
    if (e.key === 'ArrowLeft' && index > 0) {
      inputRefs.current[index - 1]?.focus();
    }
    if (e.key === 'ArrowRight' && index < length - 1) {
      inputRefs.current[index + 1]?.focus();
    }
    // If a digit is pressed and current input is full, move to next
    if (/^\d$/.test(e.key) && otp[index] !== '' && index < length - 1) {
        e.preventDefault(); // Prevent typing in the current full input
        inputRefs.current[index + 1]?.focus();
        const newOtp = [...otp];
        newOtp[index + 1] = e.key; // Place the digit in the next input
        setOtp(newOtp);
    }
  };

  const handlePaste = (e: React.ClipboardEvent<HTMLInputElement>): void => {
    e.preventDefault();
    const pastedData = e.clipboardData
      .getData('text/plain')
      .trim()
      .slice(0, length); // Get data, trim, and slice to OTP length

    const newOtp = new Array(length).fill('');
    let pasteIndex = 0;
    for (let i = 0; i < pastedData.length; i++) {
      if (/^\d$/.test(pastedData[i])) {
        newOtp[pasteIndex++] = pastedData[i];
      }
    }
    setOtp(newOtp);

    // Focus the last filled input or the last input if all are filled
    const lastFilledIndex = Math.min(pasteIndex === 0 ? 0 : pasteIndex - 1, length - 1);
    inputRefs.current[lastFilledIndex]?.focus();
  };

  const handleSubmit = (): void => {
    const fullOtp = otp.join('');
    if (fullOtp.length === length) {
      onSubmit(fullOtp);
    } else {
      // This case should ideally be prevented by button `disabled` state,
      // but good for explicit handling or if length changes dynamically.
      console.log('OTP is not complete. Please enter all digits.');
    }
  };

  const isOtpComplete = otp.every((digit) => digit !== '');

  return (
    <div className="flex flex-col items-center space-y-4 p-6 rounded-lg shadow-xl bg-white max-w-sm mx-auto border border-gray-200">
      <p className="text-gray-800 text-2xl font-semibold mb-2">Enter OTP</p>
      <div className="flex space-x-3 mb-4">
        {otp.map((digit, index) => (
          <input
            key={index}
            type="text" // Use 'text' to allow string manipulation, but handle numeric input
            maxLength={1}
            value={digit}
            onChange={(e) => handleChange(e, index)}
            onKeyDown={(e) => handleKeyDown(e, index)}
            onPaste={handlePaste}
            ref={(el) => (inputRefs.current[index] = el)}
            className="w-14 h-14 text-center text-3xl font-extrabold border-2 border-gray-300 rounded-lg focus:border-blue-500 focus:ring-blue-500 focus:ring-2 transition-all duration-200 outline-none shadow-sm"
            inputMode="numeric" // Hint for mobile keyboards
            pattern="[0-9]" // HTML5 pattern for validation
            autoComplete="one-time-code" // Autocomplete for OTP fields
            aria-label={`OTP digit ${index + 1}`}
          />
        ))}
      </div>
      {error && <p className="text-red-600 text-sm mt-2 font-medium">{error}</p>}
      <button
        onClick={handleSubmit}
        disabled={!isOtpComplete || isLoading}
        className={`w-full py-3 px-4 rounded-lg text-white font-bold text-lg transition-colors duration-200 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-opacity-50
          ${
            !isOtpComplete || isLoading
              ? 'bg-blue-400 cursor-not-allowed'
              : 'bg-blue-600 hover:bg-blue-700 active:bg-blue-800'
          }
        `}
      >
        {isLoading ? (
          <div className="flex items-center justify-center">
            <svg className="animate-spin h-5 w-5 mr-3 text-white" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24">
              <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
              <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
            </svg>
            Submitting...
          </div>
        ) : (
          'Verify OTP'
        )}
      </button>
    </div>
  );
};

export default OtpInput;
"
}
