I'll create the `OtpInput` component for your frontend React project. This robust implementation handles digit-only input, automatic focus navigation, paste functionality, and calls the `onComplete` callback when all digits are correctly entered.

```json
{
  "operation": "create",
  "path": "src/components/OtpInput.tsx",
  "content": "import React, { useState, useRef, ChangeEvent, KeyboardEvent } from 'react';

interface OtpInputProps {
  length?: number; // Default to 6
  onComplete: (otp: string) => void;
  disabled?: boolean;
}

const OtpInput: React.FC<OtpInputProps> = ({ length = 6, onComplete, disabled = false }) => {
  const [otp, setOtp] = useState<string[]>(new Array(length).fill(''));
  const inputRefs = useRef<(HTMLInputElement | null)[]>([]);

  const handleChange = (e: ChangeEvent<HTMLInputElement>, index: number) => {
    const value = e.target.value;
    // Only allow digits
    if (/[^0-9]/.test(value)) return;

    const newOtp = [...otp];
    // Take only the last character if multiple are pasted or typed very fast
    newOtp[index] = value.slice(-1);
    setOtp(newOtp);

    // Move focus to next input if a digit was entered and it's not the last input
    if (value && index < length - 1) {
      inputRefs.current[index + 1]?.focus();
    } else if (newOtp.every(digit => digit !== '')) {
      // If all digits are filled and it's the last input, trigger onComplete
      onComplete(newOtp.join(''));
    }
  };

  const handleKeyDown = (e: KeyboardEvent<HTMLInputElement>, index: number) => {
    // Move focus to previous input on backspace if current input is empty
    if (e.key === 'Backspace' && otp[index] === '' && index > 0) {
      e.preventDefault(); // Prevent default backspace behavior (e.g., navigating back in browser)
      inputRefs.current[index - 1]?.focus();
      // Clear the previous input as well
      const newOtp = [...otp];
      newOtp[index - 1] = ''; 
      setOtp(newOtp);
    } else if (e.key === 'ArrowLeft' && index > 0) {
      inputRefs.current[index - 1]?.focus();
    } else if (e.key === 'ArrowRight' && index < length - 1) {
      inputRefs.current[index + 1]?.focus();
    } else if (e.key === 'Delete' && otp[index] !== '') { // Handle delete key
        const newOtp = [...otp];
        newOtp[index] = '';
        setOtp(newOtp);
    }
  };

  // Handle paste event
  const handlePaste = (e: React.ClipboardEvent<HTMLInputElement>) => {
    e.preventDefault();
    const pasteData = e.clipboardData.getData('text/plain').trim();
    if (!/^[0-9]+$/.test(pasteData) || pasteData.length > length) {
      // If data is not numeric or too long, don't paste
      return;
    }

    const newOtp = pasteData.split('').slice(0, length);
    // Fill the remaining spots with empty strings if pasted data is shorter than length
    while (newOtp.length < length) {
      newOtp.push('');
    }
    setOtp(newOtp);

    // Move focus to the last filled input or the next available
    const lastFilledIndex = Math.min(newOtp.length - 1, length - 1);
    inputRefs.current[lastFilledIndex]?.focus();

    // If all digits are filled after paste, trigger onComplete
    if (newOtp.every(digit => digit !== '')) {
      onComplete(newOtp.join(''));
    }
  };

  return (
    <div style={{ display: 'flex', gap: '8px' }}>
      {otp.map((digit, index) => (
        <input
          key={index}
          type="text" // Use text to control input more easily, then enforce number
          maxLength={1}
          value={digit}
          onChange={(e) => handleChange(e, index)}
          onKeyDown={(e) => handleKeyDown(e, index)}
          onPaste={handlePaste}
          ref={(el) => (inputRefs.current[index] = el)}
          disabled={disabled}
          style={{
            width: '40px',
            height: '40px',
            textAlign: 'center',
            fontSize: '1.5em',
            border: '1px solid #ccc',
            borderRadius: '4px',
            outline: 'none',
            ...(disabled && { background: '#f0f0f0', cursor: 'not-allowed' }),
          }}
          // Auto-focus the first input on mount if not disabled
          autoFocus={index === 0 && !disabled}
          inputMode="numeric" // Hint for mobile keyboards
          pattern="[0-9]" // For better mobile keyboard experience and validation
        />
      ))}
    </div>
  );
};

export default OtpInput;
