I'll create an `OtpInput` React component in `src/components/OtpInput.tsx` to handle a 6-digit OTP entry, styled with Tailwind CSS, and including TypeScript types for a robust solution. This component will manage input, focus, paste events, and submission, along with displaying loading and error states.

```json
{
  "operation": "create",
  "path": "src/components/OtpInput.tsx",
  "content": "import React, { useState, useRef, useEffect, ChangeEvent, KeyboardEvent } from 'react';

interface OtpInputProps {
  length?: number; // Defaults to 6
  onSubmit: (otp: string) => void;
  isLoading?: boolean;
  error?: string | null;
}

const OtpInput: React.FC<OtpInputProps> = ({ length = 6, onSubmit, isLoading = false, error = null }) => {
  const [otp, setOtp] = useState<string[]>(new Array(length).fill(''));
  const inputRefs = useRef<Array<HTMLInputElement | null>>(new Array(length).fill(null));

  useEffect(() => {
    // Focus the first input on mount
    if (inputRefs.current[0]) {
      inputRefs.current[0].focus();
    }
  }, []);

  const handleChange = (element: HTMLInputElement, index: number) => {
    if (isNaN(Number(element.value))) return; // Only allow numbers

    const newOtp = [...otp];
    // Take only the last character if multiple characters are pasted/typed rapidly
    newOtp[index] = element.value[element.value.length - 1] || ''; 

    setOtp(newOtp);

    // Move to next input if a digit is entered and it's not the last input
    if (element.value !== '' && index < length - 1) {
      inputRefs.current[index + 1]?.focus();
    }
  };

  const handleKeyDown = (element: HTMLInputElement, index: number, e: KeyboardEvent<HTMLInputElement>) => {
    if (e.key === 'Backspace') {
      if (otp[index] === '' && index > 0) {
        // If current input is empty, move focus to previous and clear it
        inputRefs.current[index - 1]?.focus();
        const newOtp = [...otp];
        newOtp[index - 1] = '';
        setOtp(newOtp);
      } else if (otp[index] !== '') {
        // Clear current input
        const newOtp = [...otp];
        newOtp[index] = '';
        setOtp(newOtp);
      }
    } else if (e.key === 'ArrowLeft' && index > 0) {
      inputRefs.current[index - 1]?.focus();
    } else if (e.key === 'ArrowRight' && index < length - 1) {
      inputRefs.current[index + 1]?.focus();
    } else if (e.key >= '0' && e.key <= '9') {
      // Allow number input and prevent default to avoid double character insertion 
      // (handleChange will be called via onChange, which updates state)
      e.preventDefault(); 
      handleChange(element, index);
      // Focus will be moved by handleChange if not the last input
    } else if (e.key === 'Delete') {
        // Clear current input without moving focus back (unlike Backspace)
        const newOtp = [...otp];
        newOtp[index] = '';
        setOtp(newOtp);
    }
  };

  const handlePaste = (e: React.ClipboardEvent<HTMLInputElement>) => {
    e.preventDefault();
    const pasteData = e.clipboardData.getData('text').slice(0, length); // Get data and truncate to OTP length

    // Only proceed if pasteData contains only digits
    if (!/^[0-9]+$/.test(pasteData)) return;

    const newOtpArray = pasteData.split('');
    const updatedOtp = [...newOtpArray, ...new Array(length - newOtpArray.length).fill('')];

    setOtp(updatedOtp);

    // Focus the last filled input or the last input if all are filled
    const lastFilledIndex = Math.min(newOtpArray.length - 1, length - 1);
    if (inputRefs.current[lastFilledIndex]) {
      inputRefs.current[lastFilledIndex]?.focus();
    }
  };

  const isOtpComplete = otp.every(digit => digit !== '');

  const handleSubmit = () => {
    if (isOtpComplete && !isLoading) {
      onSubmit(otp.join(''));
    }
  };

  return (
    <div className="flex flex-col items-center justify-center p-4">
      <h2 className="text-2xl font-semibold mb-6 text-gray-800">Enter OTP</h2>
      <div className="flex space-x-2 mb-6">
        {otp.map((digit, index) => (
          <input
            key={index}
            type="text" // Using text to allow custom control over input and paste
            maxLength={1}
            value={digit}
            onChange={(e: ChangeEvent<HTMLInputElement>) => handleChange(e.target, index)}
            onKeyDown={(e: KeyboardEvent<HTMLInputElement>) => handleKeyDown(e.target as HTMLInputElement, index, e)}
            onPaste={handlePaste}
            ref={el => (inputRefs.current[index] = el)}
            className="w-12 h-12 text-center text-2xl font-bold border border-gray-300 rounded-md shadow-sm
                       focus:border-blue-500 focus:ring focus:ring-blue-200 focus:ring-opacity-50 outline-none
                       transition duration-150 ease-in-out disabled:bg-gray-100 disabled:text-gray-500"
            disabled={isLoading}
            inputMode="numeric" // Optimize for mobile numeric keyboard
            autoComplete="one-time-code" // Hint to browsers for OTP autofill
          />
        ))}
      </div>
      {error && (
        <p className="text-red-600 text-sm mb-4 transition-opacity duration-300 ease-in-out opacity-100 animate-fade-in">
          {error}
        </p>
      )}
      <button
        onClick={handleSubmit}
        disabled={!isOtpComplete || isLoading}
        className={`px-6 py-3 rounded-md text-white font-medium transition duration-150 ease-in-out
          ${(!isOtpComplete || isLoading)
            ? 'bg-blue-300 cursor-not-allowed'
            : 'bg-blue-600 hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-opacity-50'
          }`}
      >
        {isLoading ? 'Verifying...' : 'Verify OTP'}
      </button>
    </div>
  );
};

export default OtpInput;
"
}
