  "content": "/**
 * Generates a random hexadecimal color string (e.g., '#RRGGBB').
 * @returns {string} A random hexadecimal color string.
 */
export function randomColorHelper(): string {
  // Generate a random integer between 0 and 16777215 (0xFFFFFF)
  const randomNum = Math.floor(Math.random() * 16777216); 

  // Convert the integer to a hexadecimal string
  // padStart(6, '0') ensures the string is always 6 characters long with leading zeros if needed
  const hexColor = randomNum.toString(16).padStart(6, '0');

  // Prepend '#' to form the final hexadecimal color string
  return `#${hexColor}`;
}
