/**
 * Generates random coordinates within a specified range.
 *
 * @param min - The minimum value for coordinates (inclusive). Defaults to 0.
 * @param max - The maximum value for coordinates (exclusive). Defaults to 100.
 * @returns An object containing `x` and `y` properties, each a random number.
 */
function generateRandomCoordinates(min: number = 0, max: number = 100): Coordinates {
  if (min >= max) {
    throw new Error("Min value must be less than Max value.");
  }

  const randomValue = (minValue: number, maxValue: number) => {
    return Math.floor(Math.random() * (maxValue - minValue)) + minValue;
  };

  const x = randomValue(min, max);
  const y = randomValue(min, max);

  return { x, y };
}

