 * This file contains helpers for generating random colors in a web-friendly format.
 */

/**
 * Generates a random hexadecimal color string (e.g., '#RRGGBB').
 *
 * This function creates a valid 6-digit hexadecimal color string, suitable for use
 * in CSS, SVG, Canvas, or any other context requiring standard web colors.
 * Each call returns a new, unique (with high probability) color.
 *
 * @returns {string} A string representing a random color in '#RRGGBB' format.
 *                   Example: '#a3b5c7', '#ff00ee', '#000000', '#ffffff'.
 *
 * @example
 * const color1 = getRandomColor(); // e.g., '#3a5f7b'
 * const color2 = getRandomColor(); // e.g., '#f1c40f'
 * document.body.style.backgroundColor = getRandomColor();
 */
export function getRandomColor(): string {
  // Generate a random integer between 0 (inclusive) and 16,777,216 (exclusive).
  // 16,777,216 is 0x1000000 in hexadecimal, which means it covers all 6-digit hex colors
  // from 000000 to FFFFFF.
  const randomInt = Math.floor(Math.random() * 0x1000000);

  // Convert the integer to a hexadecimal string.
  // For example, 10 (decimal) becomes "a" (hex), 255 (decimal) becomes "ff".
  let hexColor = randomInt.toString(16);

  // Pad the hexadecimal string with leading zeros until it is 6 characters long.
  // For instance, if `randomInt` was 255, `hexColor` would be "ff".
  // `padStart(6, '0')` would transform "ff" into "0000ff".
  // This ensures a consistent 6-digit hex format required for #RRGGBB colors.
  hexColor = hexColor.padStart(6, '0');

  // Prepend '#' to the hexadecimal string to form a valid CSS color string.
  return `#${hexColor}`;
}

