      <button
        onClick={handleSubmit}
        disabled={!isOtpComplete || isLoading}
        className={`w-full py-3 px-4 rounded-lg text-white font-bold text-lg transition-colors duration-200 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-opacity-50
