    newOtp[index] = value.slice(-1);
    setOtp(newOtp);

    // Move focus to next input if a digit was entered and it's not the last input
    if (value && index < length - 1) {
      inputRefs.current[index + 1]?.focus();
    } else if (newOtp.every(digit => digit !== '')) {
      // If all digits are filled and it's the last input, trigger onComplete
      onComplete(newOtp.join(''));
    }
  };

  const handleKeyDown = (e: KeyboardEvent<HTMLInputElement>, index: number) => {
    // Move focus to previous input on backspace if current input is empty
    if (e.key === 'Backspace' && otp[index] === '' && index > 0) {
      e.preventDefault(); // Prevent default backspace behavior (e.g., navigating back in browser)
      inputRefs.current[index - 1]?.focus();
      // Clear the previous input as well
      const newOtp = [...otp];
      newOtp[index - 1] = ''; 
      setOtp(newOtp);
