```json
{
  "operation": "create",
  "path": "src/components/OtpInput.tsx",
  "content": "import React, { useState, useRef, useEffect, ChangeEvent, KeyboardEvent } from 'react';

interface OtpInputProps {
  length?: number; // Defaults to 6
  onSubmit: (otp: string) => void;
  isLoading?: boolean;
  error?: string | null;
}

const OtpInput: React.FC<OtpInputProps> = ({ length = 6, onSubmit, isLoading = false, error = null }) => {
  const [otp, setOtp] = useState<string[]>(new Array(length).fill(''));
  const inputRefs = useRef<Array<HTMLInputElement | null>>(new Array(length).fill(null));

  useEffect(() => {
    // Focus the first input on mount
    if (inputRefs.current[0]) {
      inputRefs.current[0].focus();
    }
  }, []);

  const handleChange = (element: HTMLInputElement, index: number) => {
    if (isNaN(Number(element.value))) return; // Only allow numbers

    const newOtp = [...otp];
    // Take only the last character if multiple characters are pasted/typed rapidly
    newOtp[index] = element.value[element.value.length - 1] || ''; 

    setOtp(newOtp);

    // Move to next input if a digit is entered and it's not the last input
    if (element.value !== '' && index < length - 1) {
      inputRefs.current[index + 1]?.focus();
    }
  };

  const handleKeyDown = (element: HTMLInputElement, index: number, e: KeyboardEvent<HTMLInputElement>) => {
    if (e.key === 'Backspace') {
      if (otp[index] === '' && index > 0) {
        // If current input is empty, move focus to previous and clear it
        inputRefs.current[index - 1]?.focus();
        const newOtp = [...otp];
        newOtp[index - 1] = '';
        setOtp(newOtp);
      } else if (otp[index] !== '') {
        // Clear current input
        const newOtp = [...otp];
        newOtp[index] = '';
        setOtp(newOtp);
      }
    } else if (e.key === 'ArrowLeft' && index > 0) {
      inputRefs.current[index - 1]?.focus();
    } else if (e.key === 'ArrowRight' && index < length - 1) {
      inputRefs.current[index + 1]?.focus();
    } else if (e.key >= '0' && e.key <= '9') {
      // Allow number input and prevent default to avoid double character insertion 
      // (handleChange will be called via onChange, which updates state)
      e.preventDefault(); 
      handleChange(element, index);
      // Focus will be moved by handleChange if not the last input
    } else if (e.key === 'Delete') {
        // Clear current input without moving focus back (unlike Backspace)
        const newOtp = [...otp];
        newOtp[index] = '';
        setOtp(newOtp);
    }
  };

  const handlePaste = (e: React.ClipboardEvent<HTMLInputElement>) => {
    e.preventDefault();
    const pasteData = e.clipboardData.getData('text').slice(0, length); // Get data and truncate to OTP length

    // Only proceed if pasteData contains only digits
    if (!/^[0-9]+$/.test(pasteData)) return;

    const newOtpArray = pasteData.split('');
    const updatedOtp = [...newOtpArray, ...new Array(length - newOtpArray.length).fill('')];

    setOtp(updatedOtp);

    // Focus the last filled input or the last input if all are filled
    const lastFilledIndex = Math.min(newOtpArray.length - 1, length - 1);
    if (inputRefs.current[lastFilledIndex]) {
      inputRefs.current[lastFilledIndex]?.focus();
    }
  };

  const isOtpComplete = otp.every(digit => digit !== '');

  const handleSubmit = () => {
    if (isOtpComplete && !isLoading) {
      onSubmit(otp.join(''));
    }
