{
  "operation": "create",
  "path": "src/webview/src/components/OTPInput.tsx",
  "content": "import React, { useState, useRef, useEffect } from 'react';

interface OTPInputProps {
  length?: number;
  onComplete: (otp: string) => void;
  isError?: boolean;
  isLoading?: boolean;
}

const OTPInput: React.FC<OTPInputProps> = ({ length = 6, onComplete, isError = false, isLoading = false }) => {
  const [otp, setOtp] = useState<string[]>(new Array(length).fill(''));
  const inputRefs = useRef<Array<HTMLInputElement | null>>([]);

  useEffect(() => {
    // Focus the first input on mount if not loading
    if (!isLoading && inputRefs.current[0]) {
      inputRefs.current[0].focus();
    }
  }, [isLoading]);

  const handleChange = (element: HTMLInputElement, index: number) => {
    if (isNaN(Number(element.value))) return; // Only allow numbers

    const newOtp = [...otp];
    // Ensure only one digit is set per input
    newOtp[index] = element.value.slice(-1); 
    setOtp(newOtp);

    // Focus next input if a digit is entered and it's not the last input
    if (element.value !== '' && index < length - 1) {
      inputRefs.current[index + 1]?.focus();
    } else if (index === length - 1 && newOtp.every(digit => digit !== '')) {
      // If it's the last input and all digits are entered, blur and trigger onComplete
      element.blur();
      onComplete(newOtp.join(''));
    }
  };

  const handleKeyDown = (element: HTMLInputElement, index: number, e: React.KeyboardEvent<HTMLInputElement>) => {
    if (e.key === 'Backspace') {
      if (element.value === '' && index > 0) {
        // If current input is empty, move focus to previous and clear it
        const newOtp = [...otp];
        newOtp[index - 1] = '';
        setOtp(newOtp);
        inputRefs.current[index - 1]?.focus();
      } else if (element.value !== '') {
        // If current input has a value, just clear it
        const newOtp = [...otp];
        newOtp[index] = '';
        setOtp(newOtp);
      }
    }
  };

  const handlePaste = (e: React.ClipboardEvent<HTMLInputElement>) => {
    e.preventDefault();
    const pastedData = e.clipboardData.getData('text/plain').trim();
    if (pastedData.match(/^[0-9]+$/) && pastedData.length <= length) {
      const newOtp = new Array(length).fill('');
      pastedData.split('').forEach((char, i) => {
        newOtp[i] = char;
      });
      setOtp(newOtp);
      // Focus the last input field if the pasted OTP fills all fields
      if (pastedData.length === length) {
        inputRefs.current[length - 1]?.focus();
        onComplete(newOtp.join(''));
      } else {
        inputRefs.current[pastedData.length]?.focus();
      }
    }
  };

  const combinedOtp = otp.join('');
  const isOtpComplete = combinedOtp.length === length;

  return (
    <div style={{ display: 'flex', gap: '8px', flexDirection: 'column', alignItems: 'center' }}>
      <div style={{ display: 'flex', gap: '8px' }}>
        {otp.map((digit, index) => (
          <input
            key={index}
            type="text" // Using 'text' allows for single-digit input more easily and handles paste better
            inputMode="numeric" // Suggests numeric keyboard on mobile
            pattern="[0-9]*" // Enforces numeric input on browsers that support it
            maxLength={1}
            value={digit}
            onChange={(e) => handleChange(e.target, index)}
            onKeyDown={(e) => handleKeyDown(e.target as HTMLInputElement, index, e)}
            onPaste={handlePaste}
            ref={(el) => (inputRefs.current[index] = el)}
            style={{
              width: '40px',
              height: '40px',
              textAlign: 'center',
              fontSize: '20px',
              border: `1px solid ${isError ? 'red' : '#ccc'}`, // Conditional error styling
              borderRadius: '4px',
              outline: 'none',
              transition: 'border-color 0.2s ease, background-color 0.2s ease',
              backgroundColor: isLoading ? '#f0f0f0' : 'white',
              boxSizing: 'border-box',
            }}
            disabled={isLoading}
          />
        ))}
      </div>
      {isError && (
        <p style={{ color: 'red', fontSize: '12px', marginTop: '4px' }}>Invalid OTP. Please try again.</p>
      )}
      <button
        onClick={() => isOtpComplete && onComplete(combinedOtp)}
        disabled={!isOtpComplete || isLoading}
        style={{
          marginTop: '16px',
          padding: '10px 20px',
          backgroundColor: isOtpComplete && !isLoading ? '#007bff' : '#a0c7ff',
          color: 'white',
          border: 'none',
          borderRadius: '4px',
          cursor: isOtpComplete && !isLoading ? 'pointer' : 'not-allowed',
          fontSize: '16px',
          opacity: (!isOtpComplete || isLoading) ? 0.7 : 1, // Visual cue for disabled state
        }}
      >
        {isLoading ? 'Submitting...' : 'Submit OTP'}
      </button>
    </div>
  );
};

export default OTPInput;
"
}
