/**
 * Generates a random hexadecimal color string.
 * Each call will produce a new, unique color from the full RGB spectrum (0x000000 to 0xFFFFFF).
 *
 * This function is useful for dynamic styling, chart visualizations, or assigning
 * distinct colors to different items (e.g., log levels, file types, user sessions)
 * in a user interface, especially within a VS Code Webview.
 *
 * @returns {string} A 7-character hexadecimal color string (e.g., "#RRGGBB").
 *                   The '#' prefix is included, making it directly usable in CSS or HTML.
 *
 * @example
 * // In a Webview script:
 * const myDiv = document.createElement('div');
 * myDiv.style.backgroundColor = getRandomHexColor(); // e.g., sets background to "#a3e5c7"
 * document.body.appendChild(myDiv);
 *
 * @example
 * // In a VS Code command:
 * import * as vscode from 'vscode';
 * import { getRandomHexColor } from '../utils/colorHelpers';
 *
 * export function activate(context: vscode.ExtensionContext) {
 *   let disposable = vscode.commands.registerCommand('myExtension.showRandomColor', () => {
 *     const randomColor = getRandomHexColor();
 *     vscode.window.showInformationMessage(`Generated color: ${randomColor}`);
 *   });
 *   context.subscriptions.push(disposable);
 * }
 */
export function getRandomHexColor(): string {
    try {
        // Generate a random integer for each RGB component (0-255).
        // Math.random() returns a float in [0, 1). Multiplying by 256 gives [0, 256).
        // Math.floor truncates to an integer, resulting in a number in [0, 255].
        const r = Math.floor(Math.random() * 256);
        const g = Math.floor(Math.random() * 256);
        const b = Math.floor(Math.random() * 256);

        // Convert each component to a two-digit hexadecimal string.
        // .toString(16) converts the number to its hexadecimal representation.
        // .padStart(2, '0') ensures the string is exactly two characters long,
        // padding with a leading '0' if the hex value is single digit (e.g., 'A' becomes '0A').
        const hexR = r.toString(16).padStart(2, '0');
        const hexG = g.toString(16).padStart(2, '0');
        const hexB = b.toString(16).padStart(2, '0');

        // Combine the hexadecimal components with a '#' prefix to form the final color string.
        return `#${hexR}${hexG}${hexB}`;
    } catch (error) {
        // In a production environment, you might log this error to a telemetry service
        // or to the VS Code output channel if this function were part of a more complex
        // color generation logic that could fail.
        // For a simple Math.random() operation, an error here is highly unlikely
        // unless the Math object itself is corrupted or overridden.
        console.error('Failed to generate random hex color:', error);
        // Fallback to a default color in case of an unexpected error
        return '#808080'; // A neutral gray as a safe fallback
    }
}

/**
 * An alternative implementation that generates a random color by converting a
 * single large random number to hexadecimal. This is often more concise.
 *
 * @returns {string} A 7-character hexadecimal color string (e.g., "#RRGGBB").
 */
export function getRandomHexColorConcise(): string {
    try {
        // Generate a random integer between 0 and 16777215 (0xFFFFFF).
        // (Math.random() * 0xFFFFFF) gives a float in [0, 0xFFFFFF).
        // Math.floor truncates it to an integer in [0, 0xFFFFFF].
        const color = Math.floor(Math.random() * 0xFFFFFF);

        // Convert the number to a hexadecimal string.
        // .toString(16) performs the conversion.
        // .padStart(6, '0') ensures the hex string is 6 characters long, padding
        // with leading zeros if the number is small (e.g., 0xFF becomes "0000FF").
        const hexColor = color.toString(16).padStart(6, '0');

        return `#${hexColor}`;
    } catch (error) {
        console.error('Failed to generate concise random hex color:', error);
        return '#808080'; // Fallback to gray
    }
}

// You might also consider helpers for other color formats if needed, e.g., RGB.
/**
 * Generates a random RGB color string.
 * @returns {string} An RGB color string (e.g., "rgb(255, 128, 0)").
 */
export function getRandomRgbColor(): string {
    try {
        const r = Math.floor(Math.random() * 256);
        const g = Math.floor(Math.random() * 256);
        const b = Math.floor(Math.random() * 256);
        return `rgb(${r}, ${g}, ${b})`;
    } catch (error) {
        console.error('Failed to generate random RGB color:', error);
        return 'rgb(128, 128, 128)'; // Fallback to gray
    }
}
