import * as fs from 'fs/promises'; // Import the promise-based file system module for asynchronous operations
import * as path from 'path';     // Import the path module for handling and normalizing file paths

/**
 * Creates a new file at the specified path with the given content.
 * This function handles creating necessary parent directories if they don't exist.
 * If a file already exists at the given path, its content will be overwritten.
 *
 * @param filePath The full path to the new file, including the file name and its extension.
 *                 Example: 'src/components/NewComponent.tsx' or 'config/settings.json'.
 * @param content The string content to write into the new file. Defaults to an empty string.
 * @returns A Promise that resolves to `true` if the file was created or updated successfully,
 *          or `false` if an error occurred during the process.
 */
export async function createNewFile(filePath: string, content: string = ''): Promise<boolean> {
    try {
        // Extract the directory path from the full file path.
        const directoryPath = path.dirname(filePath);

        // Ensure that all necessary parent directories exist.
        // `fs.mkdir` with `recursive: true` will create intermediate directories
        // and will not throw an error if the directory already exists.
        await fs.mkdir(directoryPath, { recursive: true });

        // Write the provided content to the file.
        // `fs.writeFile` will create the file if it doesn't exist,
        // or overwrite it if it does. Using 'utf8' encoding for standard text files.
        await fs.writeFile(filePath, content, { encoding: 'utf8' });

        console.log(` Successfully created or updated file: ${filePath}`);
        return true;
    } catch (error) {
        // Log any errors that occur during file creation or directory creation.
        // Using `instanceof Error` for robust error message extraction.
        console.error(` Error creating file "${filePath}":`, error instanceof Error ? error.message : String(error));
        return false;
    }
}

