/**
 * Calculates the sum of three numeric values.
 *
 * This function is designed for production use, ensuring robustness and type safety.
 * It strictly validates inputs to be finite numbers, preventing unexpected behaviors
 * from `NaN`, `Infinity`, or non-numeric types.
 *
 * @param {number} a - The first number to be summed.
 * @param {number} b - The second number to be summed.
 * @param {number} c - The third number to be summed.
 * @returns {number} The sum of the three finite numbers.
 * @throws {TypeError} If any of the provided inputs is not a finite number.
 *   This ensures that the function operates only on valid numeric data,
 *   preventing issues like `NaN` propagation or unexpected `Infinity` results.
 *
 * @example
 * // Valid usage:
 * const result1 = sumThreeValues(10, 20, 30);
 * console.log(result1); // Output: 60
 *
 * const result2 = sumThreeValues(-5, 0, 15.5);
 * console.log(result2); // Output: 10.5
 *
 * @example
 * // Error handling for invalid inputs:
 * try {
 *   sumThreeValues(1, 2, NaN);
 * } catch (error) {
 *   console.error(error.message); // Output: All inputs must be finite numbers.
 * }
 *
 * try {
 *   sumThreeValues(1, Infinity, 3);
 * } catch (error) {
 *   console.error(error.message); // Output: All inputs must be finite numbers.
 * }
 *
 * try {
 *   // TypeScript would typically catch this at compile-time,
 *   // but this demonstrates runtime protection for dynamically typed scenarios.
 *   // @ts-ignore - For demonstration of runtime error if type safety is bypassed.
 *   sumThreeValues(1, 2, "3");
 * } catch (error) {
 *   console.error(error.message); // Output: All inputs must be finite numbers.
 * }
 */
export const sumThreeValues = (a: number, b: number, c: number): number => {
  // Input validation: Ensure all parameters are finite numbers.
  // Number.isFinite() checks for numbers that are not NaN, Infinity, or -Infinity.
  // This provides a robust check beyond basic typeof number, guarding against
  // potentially problematic numeric values and non-numeric types that might
  // bypass type checks in dynamic contexts (e.g., JavaScript execution, 'any' types).
  if (!Number.isFinite(a) || !Number.isFinite(b) || !Number.isFinite(c)) {
    throw new TypeError('All inputs must be finite numbers.');
  }

  // Perform the summation.
  // Since inputs are validated to be finite numbers, standard addition is safe.
  return a + b + c;
};
