/**
 * Interface for options that can be passed to the sanitizeString function.
 * This allows for flexible configuration of the sanitization process.
 */
export interface SanitizeStringOptions {
  /**
   * If true, leading and trailing whitespace will be removed. Defaults to true.
   */
  trim?: boolean;
  /**
   * If true, multiple consecutive spaces will be replaced with a single space. Defaults to false.
   */
  collapseWhitespace?: boolean;
  /**
