   * Useful for more complex character removal patterns (e.g., special symbols, non-alphanumeric).
   */
  removePattern?: RegExp;
}

/**
 * Sanitizes a given string based on provided options.
 * This function is highly versatile for cleaning user input, preparing strings for display,
 * or normalizing data across the application.
 *
 * @param inputString The string to sanitize. Can be null or undefined, which will result in an empty string.
 * @param options Configuration for the sanitization process.
 * @returns The sanitized string. Returns an empty string for null/undefined input.
 */
export function sanitizeString(inputString: string | null | undefined, options?: SanitizeStringOptions): string {
  // Handle null or undefined input gracefully, returning an empty string.
  // A console warning is logged to indicate that input was not a string.
  if (inputString === null || inputString === undefined) {
    console.warn("sanitizeString received null or undefined input and will return an empty string.");
    return "";
  }

  let result = String(inputString); // Ensure the input is treated as a string

  // Merge default options with any provided options
  const effectiveOptions: SanitizeStringOptions = {
    trim: true, // Default: remove leading/trailing whitespace
    collapseWhitespace: false, // Default: do not collapse multiple spaces
    ...options, // Override defaults with user-specified options
  };

  // Step 1: Trim whitespace if enabled
  if (effectiveOptions.trim) {
    result = result.trim();
  }

  // Step 2: Collapse multiple spaces into single spaces if enabled
  if (effectiveOptions.collapseWhitespace) {
    result = result.replace(/\s+/g, ' '); // Regex: one or more whitespace characters
  }

  // Step 3: Remove specified characters if provided
  if (effectiveOptions.removeChars && effectiveOptions.removeChars.length > 0) {
    // Escape special regex characters in each char to be removed to prevent regex injection issues
    const charsToEscapeAndRemove = effectiveOptions.removeChars.map(char => escapeRegExp(char));
    // Create a character class regex like /[abc]/
    const regex = new RegExp(`[${charsToEscapeAndRemove.join('')}]`, 'g');
    result = result.replace(regex, '');
  }

  // Step 4: Remove characters matching a custom regex pattern if provided
  if (effectiveOptions.removePattern) {
    result = result.replace(effectiveOptions.removePattern, '');
  }

  return result;
}

/**
 * Converts a string to title case (e.g., "hello world" -> "Hello World").
 * Each word in the string will have its first letter capitalized and the rest lowercased.
 *
 * @param inputString The string to convert. Can be null or undefined, which will result in an empty string.
 * @returns The title-cased string. Returns an empty string for null/undefined input.
 */
export function toTitleCase(inputString: string | null | undefined): string {
  // Handle null or undefined input gracefully, returning an empty string.
  if (inputString === null || inputString === undefined) {
    return "";
  }
  // Convert to string and apply title case regex replacement
  return String(inputString).replace(/\w\S*/g, (word) => {
    return word.charAt(0).toUpperCase() + word.substring(1).toLowerCase();
  });
}

/**
 * Helper function to escape special characters in a string for safe use within a regular expression.
 * This prevents characters like '.', '*', '+', '?', etc., from being interpreted as regex operators.
 *
 * @param str The string containing potential regex special characters.
 * @returns The escaped string, safe for inclusion in a `new RegExp()`.
 */
function escapeRegExp(str: string): string {
  // Escape characters that have special meaning in regex
  return str.replace(/[.*+?^${}()|[\]\\]/g, '\\$&'); // $& means the entire matched substring
}
