 * Formats a person's name into a readable string based on specified options.
 *
 * This function provides flexibility in how a name is displayed, supporting
 * different common formatting conventions.
 *
 * @param {PersonName} person - The person's name object. Must include at least firstName and lastName.
 * @param {boolean} [includeSalutation=true] - Whether to prepend the salutation (if present) to the name. Defaults to true.
 * @param {boolean} [lastNameFirst=false] - Whether to format the name as "LastName, FirstName MiddleName". Defaults to false (i.e., "FirstName MiddleName LastName").
 * @returns {string} The formatted name string.
 * @throws {Error} If `person.firstName` or `person.lastName` is missing, as these are mandatory for valid name formatting.
 *
 * @example
 * const p1 = { firstName: 'John', lastName: 'Doe', salutation: 'Mr.' };
 * formatPersonName(p1); // "Mr. John Doe"
 *
 * const p2 = { firstName: 'Alice', middleName: 'Marie', lastName: 'Smith' };
 * formatPersonName(p2, false); // "Alice Marie Smith"
 *
 * const p3 = { firstName: 'Jane', lastName: 'Austen' };
 * formatPersonName(p3, true, true); // "Austen, Jane"
 */
export function formatPersonName(
  person: PersonName,
  includeSalutation: boolean = true,
  lastNameFirst: boolean = false
): string {
  // --- Error Handling ---
  if (!person.firstName || !person.lastName) {
    throw new Error("First name and last name are required to format a person's name.");
  }

  const parts: string[] = [];

  // --- Salutation Handling ---
  if (includeSalutation && person.salutation) {
    parts.push(person.salutation);
  }

  // --- Name Order Handling ---
  if (lastNameFirst) {
    parts.push(person.lastName + ',');
    parts.push(person.firstName);
    if (person.middleName) {
      parts.push(person.middleName); // Middle name after first name when last name is first
    }
  } else {
    parts.push(person.firstName);
    if (person.middleName) {
      parts.push(person.middleName);
    }
    parts.push(person.lastName);
  }

  // --- Return Formatted String ---
  return parts.join(' ');
}

