 * @example
 * // Basic usage
 * const result1 = sum(5, 3); // 8
 *
 * @example
 * // Handling negative numbers
 * const result2 = sum(-10, 20); // 10
 *
 * @example
 * // Handling floating point numbers
 * const result3 = sum(0.1, 0.2); // 0.30000000000000004 (due to floating point precision)
 *
 * @example
 * // Handling Infinity
 * const result4 = sum(100, Infinity); // Infinity
 * const result5 = sum(Infinity, Infinity); // Infinity
 *
 * @example
 * // Error handling for non-numeric inputs
 * try {
 *   sum(10, 'abc');
 * } catch (error) {
 *   console.error(error.message); // Output: Invalid arguments: Both inputs must be of type "number".
 * }
 *
 * @example
 * // Error handling for NaN inputs
 * try {
 *   sum(5, NaN);
 * } catch (error) {
 *   console.error(error.message); // Output: Invalid arguments: Inputs cannot be NaN (Not a Number).
 * }
 */
export function sum(a: number, b: number): number {
  // --- Input Validation ---
  // Even with TypeScript type annotations, runtime checks are crucial for production-ready
  // JavaScript code. This helps catch errors in scenarios like:
  // 1. When the function is called from plain JavaScript files (bypassing TS type checks).
  // 2. When type assertions (`as any`) are used incorrectly by other developers.
  // 3. Ensuring robustness in environments where types might be less strictly enforced.

  // 1. Check if inputs are actually numbers using `typeof`.
  if (typeof a !== 'number' || typeof b !== 'number') {
    throw new TypeError('Invalid arguments: Both inputs must be of type "number".');
  }

  // 2. Check for NaN (Not a Number) specifically. `NaN` is technically `typeof 'number'`,
  //    but it represents an invalid numeric value for most arithmetic operations.
  //    `Number.isNaN()` is preferred over global `isNaN()` as it doesn't coerce non-numeric values.
  if (Number.isNaN(a) || Number.isNaN(b)) {
    throw new Error('Invalid arguments: Inputs cannot be NaN (Not a Number).');
  }

  // --- Core Logic ---
  // Perform the addition. JavaScript handles standard numeric operations,
  // including positive/negative numbers, decimals, and Infinity according to
  // IEEE 754 floating-point standard.
  return a + b;
}

