   *                    Defaults to null if not provided or invalid.
   */
  constructor(initialName: string | null = null) {
    if (typeof initialName === 'string' && initialName.trim() !== '') {
      this._userName = initialName.trim();
    } else {
      this._userName = null;
    }
  }

  /**
   * Retrieves the user's current name.
   *
   * @returns The user's name as a string if it has been set, otherwise `null`.
   */
  public getUserName(): string | null {
    return this._userName;
  }

  /**
   * Sets or updates the user's name.
   *
   * @param name The new name to set for the user.
   * @throws {Error} If the provided name is `null`, `undefined`, or an empty string
   *                 (after trimming whitespace).
   */
  public setUserName(name: string): void {
    if (typeof name !== 'string' || name.trim() === '') {
      // In a production environment, you might consider returning a boolean
      // (e.g., `false` for failure) or a more specific custom error type
      // instead of a generic `Error` for better error handling flow.
      throw new Error('User name cannot be null, undefined, or an empty string.');
    }
    this._userName = name.trim();
  }

  /**
   * Responds directly to the query "what is my name, tell my name?".
   * It provides the user's name if known, or indicates that the name is not yet known.
   *
   * @returns A conversational string containing the user's name or a message
   *          indicating that the name is unknown.
   */
  public tellMyName(): string {
    if (this._userName) {
      return `Your name is ${this._userName}.`;
    } else {
      // This is the direct answer when the system doesn't have the information.
      return `I don't know your name yet. Please tell me your name.`;
    }
  }

  /**
   * Clears the user's name from the context.
   */
  public clearUserName(): void {
    this._userName = null;
  }
}

